UPDATE JFD_Reforms
SET HelpBonus = 'TXT_KEY_JFD_REFORM_HONOURS_SCIENCES_HELP_BONUS_CP', HelpPenalty = 'TXT_KEY_JFD_REFORM_HONOURS_SCIENCES_HELP_PENALTY_CP'
WHERE Type = 'REFORM_JFD_HONOURS_SCIENCES'
AND EXISTS (SELECT Name FROM CustomModOptions WHERE Name = 'COMMUNITY_PATCH' AND Value = 1);

UPDATE JFD_Reforms
SET HelpBonus = 'TXT_KEY_JFD_REFORM_HONOURS_HUMANITIES_HELP_BONUS_CP', HelpPenalty = 'TXT_KEY_JFD_REFORM_HONOURS_HUMANITIES_HELP_PENALTY_CP'
WHERE Type = 'REFORM_JFD_HONOURS_HUMANITIES'
AND EXISTS (SELECT Name FROM CustomModOptions WHERE Name = 'COMMUNITY_PATCH' AND Value = 1);

UPDATE JFD_Reforms
SET HelpPenalty = 'TXT_KEY_JFD_REFORM_HONOURS_SCIENCES_HELP_PENALTY_MAGISTRATE'
WHERE Type = 'REFORM_JFD_HONOURS_SCIENCES'
AND EXISTS (SELECT Name FROM CustomModOptions WHERE Name = 'COMMUNITY_PATCH' AND Value = 1)
AND EXISTS (SELECT Type FROM Specialists WHERE Type = 'SPECIALIST_JFD_MAGISTRATE');

UPDATE JFD_Reforms
SET HelpBonus = 'TXT_KEY_JFD_REFORM_HONOURS_HUMANITIES_HELP_BONUS_MAGISTRATE'
WHERE Type = 'REFORM_JFD_HONOURS_HUMANITIES'
AND EXISTS (SELECT Name FROM CustomModOptions WHERE Name = 'COMMUNITY_PATCH' AND Value = 1)
AND EXISTS (SELECT Type FROM Specialists WHERE Type = 'SPECIALIST_JFD_MAGISTRATE');
